package Seção_10;

public class Wrapper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 20;

		
		Integer obj = x;

		System.out.println(obj);

		// Com as Wrapper classes não é preciso se preocupar com conversões nem
		//castings.
		int y =  obj * 2;

		System.out.println(y);
	}

}
